package com.basic;

public class SwapTwoNumbers {

	/**
	 * @param args
	 */
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m=10,n=15;
		swappingNumbers(m,n);

	}

	private static void swappingNumbers(int m, int n) {
		// TODO Auto-generated method stub
		int temp=m;
		m=n;
		n=temp;
		System.out.println(" m ="+m+"n="+n);
		
	}

}
