package com.basic.conversation;

public class AllConversation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String longValue="8687979989898";
		//String to Long conversation
		System.out.println("String to Long :"+Long.parseLong(longValue));
		//long to String
		long lv=12345676565L;
		System.out.println("Long to String :"+String.valueOf(lv));
		
		//int to double
		int intValue=200;
		double doubleVal=intValue;
		System.out.println("Int To Double :"+doubleVal);
		
		//Double To Int
		double dValue=150.60d;
		System.out.println("Double TO Int :"+(int)dValue);
		
		
	

	}

}
