package com.basic.conversation;

public class StringToBoolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="true";
		String s2="false";
		String s3="ok";
		System.out.println(Boolean.parseBoolean(s1));
		System.out.println(Boolean.parseBoolean(s2));
		System.out.println(Boolean.parseBoolean(s3));
		
		//Boolean to String
		boolean b1=true;
		boolean b2=false;
		System.out.println(String.valueOf(b1));
		System.out.println(String.valueOf(b2));
		
		


		

	}

}
