package com.basic.conversion;

public class CharToInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char asciValue='a';
		char asciNum='1';
		int charValue=asciValue;
		int numValue=asciNum;
		System.out.println("a="+charValue);
		System.out.println("1="+numValue);
		
		//int to char
		int a=65;
		char c=(char)a;
		System.out.println("int to char "+c);

	}

}
