package com.basic.pattern;

public class PrintLeftTriangle {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=5;
		leftTriangle(k);

	}

	private static void leftTriangle(int k) {
		// TODO Auto-generated method stub
		int a,b;
		for( a=0;a<=k;a++){
			for(b=2*(k-a);b>0;b--){
				System.out.print(" ");
			}
				for(b=0;b<=a;b++){
					System.out.print("*");
				}
			System.out.println();
		}
	}

}
