package com.basic.pattern;

public class PrntRghtTrangle {

	/**
	 * @param args
	 */
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=5;
		startTriangle(k);
	}

	private static void startTriangle(int k) {
		// TODO Auto-generated method stub
	    System.out.println("k ="+k);
	    int a,b;
	    for(a=0;a<k;a++){
	    	
	    	for(b=0;b<=a;b++){
	    		
	    		System.out.print("*");
	    	}
	    	System.out.println();
	    }
	    
		
	}

}
